package page_objects.alerts_frame_windows;import com.codeborne.selenide.SelenideElement;import org.openqa.selenium.By;import page_objects.base.BasePage;import page_objects.alerts_frame_windows.alerts.AlertsPage;import page_objects.alerts_frame_windows.browser_windows.BrowserWindowsPage;import static com.codeborne.selenide.Selenide.$;import static com.codeborne.selenide.Selenide.page;public class AlertsFrameWindowsPage extends BasePage {    private final SelenideElement ALERTS_CATEGORY = $(By.            xpath("//span[@class='text' and contains(text(), 'Alerts')]"));    private final SelenideElement BROWSER_WINDOWS_CATEGORY = $(By.            xpath("//span[@class='text' and contains(text(), 'Browser Windows')]"));    public AlertsPage clickOnAlertsCategory() {        waitElementIsVisible(ALERTS_CATEGORY).scrollTo().click();        return page(AlertsPage.class);    }    public BrowserWindowsPage clickOnBrowserWindowsCategory() {        waitElementIsVisible(BROWSER_WINDOWS_CATEGORY).scrollTo().click();        return page(BrowserWindowsPage.class);    }}