package page_objects.alerts_frame_windows.browser_windows;import com.codeborne.selenide.Selenide;import com.codeborne.selenide.SelenideElement;import page_objects.alerts_frame_windows.AlertsFrameWindowsPage;import static com.codeborne.selenide.Selenide.$;import static com.codeborne.selenide.Selenide.page;public class BrowserWindowsPage extends AlertsFrameWindowsPage {    private final SelenideElement NEW_TAB_BUTTON = $("button#tabButton");    private final SelenideElement NEW_WINDOW_BUTTON = $("button#windowButton");    private final SelenideElement NEW_WINDOW_MESSAGE_BUTTON = $("button#messageWindowButton");    public BrowserWindowsPage сlickOnNewTabButton() {        waitElementIsVisible(NEW_TAB_BUTTON).scrollTo().click();        return page(BrowserWindowsPage.class);    }    public BrowserWindowsPage сlickOnNewWindowButton() {        waitElementIsVisible(NEW_WINDOW_BUTTON).scrollTo().click();        return page(BrowserWindowsPage.class);    }    public BrowserWindowsPage сlickOnNewWindowMessageButton() {        waitElementIsVisible(NEW_WINDOW_MESSAGE_BUTTON).scrollTo().click();        return page(BrowserWindowsPage.class);    }    public BrowserWindowsPage сloseNewTabOrWindow() {        Selenide.switchTo().window(1).close();        Selenide.switchTo().window(0);        return page(BrowserWindowsPage.class);    }}