package page_objects.elements;import com.codeborne.selenide.SelenideElement;import org.openqa.selenium.By;import page_objects.base.BasePage;import page_objects.elements.buttons.ButtonsPage;import page_objects.elements.textbox.TextBoxPage;import static com.codeborne.selenide.Selenide.$;import static com.codeborne.selenide.Selenide.page;public class ElementsPage extends BasePage {    private final SelenideElement BUTTONS_CATEGORY = $(By.            xpath("//span[@class='text' and contains(text(), 'Buttons')]"));    private final SelenideElement TEXTBOX_CATEGORY = $(By.            xpath("//span[@class='text' and contains(text(), 'Text Box')]"));    public ButtonsPage clickOnButtonsCategory() {        waitElementIsVisible(BUTTONS_CATEGORY).scrollTo().click();        return page(ButtonsPage.class);    }    public TextBoxPage clickOnTextboxCategory() {        waitElementIsVisible(TEXTBOX_CATEGORY).scrollTo().click();        return page(TextBoxPage.class);    }}