package page_objects.elements.buttons;import com.codeborne.selenide.SelenideElement;import page_objects.elements.ElementsPage;import static com.codeborne.selenide.Selectors.byText;import static com.codeborne.selenide.Selenide.$;import static com.codeborne.selenide.Selenide.page;public class ButtonsPage extends ElementsPage {    private final SelenideElement CLICK_ME_BUTTON = $(byText("Click Me"));    private final SelenideElement DOUBLE_CLICK_ME_BUTTON = $(            byText("Double Click Me"));    private final SelenideElement RIGHT_CLICK_ME_BUTTON = $(            byText("Right Click Me"));    public ButtonsPage checkTextThatAppears(String text) {        waitElementIsVisible(getTextElement(text)).scrollTo();        return page(ButtonsPage.class);    }    public ButtonsPage clickOnButtonClickMe() {        waitElementIsVisible(CLICK_ME_BUTTON).scrollTo().click();        return page(ButtonsPage.class);    }    public ButtonsPage clickOnButtonDoubleClickMe() {        waitElementIsVisible(DOUBLE_CLICK_ME_BUTTON).scrollTo().doubleClick();        return page(ButtonsPage.class);    }    public ButtonsPage clickOnButtonRightClickMe() {        waitElementIsVisible(RIGHT_CLICK_ME_BUTTON).scrollTo().contextClick();        return page(ButtonsPage.class);    }    private SelenideElement getTextElement(String text) {        return $(byText(text));    }}