package page_objects.elements.textbox;import com.codeborne.selenide.SelenideElement;import org.openqa.selenium.By;import page_objects.base.BasePage;import static com.codeborne.selenide.Condition.text;import static com.codeborne.selenide.Selenide.*;public class TextBoxPage extends BasePage {    private static String currentAddressText;    private static String emailText;    private static String fullNameText;    private static String permanentAddressText;    private final SelenideElement CURRENT_ADDRESS_INPUT = $(By.            id("currentAddress"));    private final SelenideElement CURRENT_ADDRESS_VALIDATE_BLOCK = $(By.            xpath("//p[@id='currentAddress']"));    private final SelenideElement EMAIL_INPUT = $(By.id("userEmail"));    private final SelenideElement EMAIL_VALIDATE_BLOCK = $(By.            xpath("//p[@id='email']"));    private final SelenideElement FULL_NAME_INPUT = $(By.id("userName"));    private final SelenideElement FULL_NAME_VALIDATE_BLOCK = $(By.            xpath("//p[@id='name']"));    private final SelenideElement PERMANENT_ADDRESS_INPUT = $(By.            id("permanentAddress"));    private final SelenideElement PERMANENT_ADDRESS_VALIDATE_BLOCK = $(By.            xpath("//p[@id='permanentAddress']"));    private final SelenideElement SUBMIT_BUTTON = $(By.id("submit"));    public TextBoxPage checkAppearData() {        this.checkTextWithValidateBlock(fullNameText, FULL_NAME_VALIDATE_BLOCK).                checkTextWithValidateBlock(emailText, EMAIL_VALIDATE_BLOCK).                checkTextWithValidateBlock(currentAddressText, CURRENT_ADDRESS_VALIDATE_BLOCK).                checkTextWithValidateBlock(permanentAddressText, PERMANENT_ADDRESS_VALIDATE_BLOCK);        return page(TextBoxPage.class);    }    public TextBoxPage clickOnSubmitButton() {        waitElementIsVisible(SUBMIT_BUTTON).scrollTo().click();        return page(TextBoxPage.class);    }    public TextBoxPage enterDataOnInputs(String fullName,                                         String email, String currentAddress,                                         String permanentAddress) {        fullNameText = fullName;        emailText = email;        currentAddressText = currentAddress;        permanentAddressText = permanentAddress;        this.choiceAndEnterTextOnInput(fullName, FULL_NAME_INPUT).                choiceAndEnterTextOnInput(email, EMAIL_INPUT).                choiceAndEnterTextOnInput(currentAddress, CURRENT_ADDRESS_INPUT).                choiceAndEnterTextOnInput(permanentAddress, PERMANENT_ADDRESS_INPUT);        return page(TextBoxPage.class);    }    private TextBoxPage checkTextWithValidateBlock(String text,                                                   SelenideElement blockWithComparisonText) {        waitElementIsVisible(blockWithComparisonText).                scrollTo().shouldBe(text(text));        return page(TextBoxPage.class);    }    private TextBoxPage choiceAndEnterTextOnInput(String text,                                                  SelenideElement input) {        waitElementIsVisible(input).scrollTo().sendKeys(text);        return page(TextBoxPage.class);    }}