package page_objects.home;import com.codeborne.selenide.Selectors;import com.codeborne.selenide.SelenideElement;import page_objects.base.BasePage;import page_objects.elements.ElementsPage;import static com.codeborne.selenide.Selenide.$;import static com.codeborne.selenide.Selenide.page;public class HomePage extends BasePage {    private final SelenideElement ELEMENTS_CARD = $(Selectors.            byText("Elements"));    public ElementsPage clickOnElementsCard() {        waitElementIsVisible(ELEMENTS_CARD).scrollTo().click();        return page(ElementsPage.class);    }}