package pages.base;import com.codeborne.selenide.Condition;import com.codeborne.selenide.Selenide;import com.codeborne.selenide.SelenideElement;import io.qameta.allure.Step;import java.time.Duration;public class BasePage {    public void open(String url) {        Selenide.open(url);    }    protected SelenideElement WaitElementIsVisible(SelenideElement element) {        element.shouldBe(Condition.visible, Duration.ofSeconds(4));        return element;    }}