package pages.demoqa_alertsFW;import com.codeborne.selenide.SelenideElement;import org.openqa.selenium.By;import pages.base.BasePage;import pages.demoqa_alertsFW.alerts.DemoqaAlertsFWAlertsPage;import pages.demoqa_alertsFW.browserWindows.DemoqaAlertsFWBrowserWindowsPage;import static com.codeborne.selenide.Selenide.$;import static com.codeborne.selenide.Selenide.page;public class DemoqaAlertsFWPage extends BasePage {    private SelenideElement browserWindowsCategory = $(By            .xpath("//span[@class='text' and contains(text(), 'Browser Windows')]"));    private SelenideElement alertsCategory = $(By            .xpath("//span[@class='text' and contains(text(), 'Alerts')]"));    public DemoqaAlertsFWBrowserWindowsPage ClickOnBrowserWindowsCategory()    {        WaitElementIsVisible(browserWindowsCategory).scrollTo().click();        return page(DemoqaAlertsFWBrowserWindowsPage.class);    }    public DemoqaAlertsFWAlertsPage ClickOnAlertsCategory()    {        WaitElementIsVisible(alertsCategory).scrollTo().click();        return page(DemoqaAlertsFWAlertsPage.class);    }}