package pages.demoqa_alertsFW.alerts;import com.codeborne.selenide.Selenide;import com.codeborne.selenide.SelenideElement;import org.openqa.selenium.By;import pages.base.BasePage;import java.time.Duration;import static com.codeborne.selenide.Condition.text;import static com.codeborne.selenide.Selectors.byText;import static com.codeborne.selenide.Selenide.*;public class DemoqaAlertsFWAlertsPage extends BasePage {    private SelenideElement getTextElement(String text) {        return $("#javascriptAlertsWrapper").shouldBe(text(text));    }    private SelenideElement getButton(String text) {        String xpath = String.format("//span[contains(text(), '%s')]/parent::div/following-sibling::div/button", text);        return $(By.xpath(xpath));    }    public DemoqaAlertsFWAlertsPage ClickButtonNextToText(String text) {        WaitElementIsVisible(getButton(text)).scrollTo().click();        return page(DemoqaAlertsFWAlertsPage.class);    }    public DemoqaAlertsFWAlertsPage CloseAlertDialogue() {        Selenide.switchTo().alert(Duration.ofSeconds(10)).dismiss();        return page(DemoqaAlertsFWAlertsPage.class);    }    public DemoqaAlertsFWAlertsPage AcceptConfirmDialogue() {        Selenide.confirm();        return page(DemoqaAlertsFWAlertsPage.class);    }    public DemoqaAlertsFWAlertsPage EnterDataOnPrompt(String text)    {        Selenide.prompt(text);        return page(DemoqaAlertsFWAlertsPage.class);    }    public DemoqaAlertsFWAlertsPage CheckText(String text)    {        WaitElementIsVisible(getTextElement(text)).scrollTo();        return page(DemoqaAlertsFWAlertsPage.class);    }}