package pages.demoqa_alertsFW.browserWindows;import com.codeborne.selenide.Selenide;import com.codeborne.selenide.SelenideElement;import pages.demoqa_alertsFW.DemoqaAlertsFWPage;import pages.demoqa_alertsFW.alerts.DemoqaAlertsFWAlertsPage;import static com.codeborne.selenide.Selenide.$;import static com.codeborne.selenide.Selenide.page;public class DemoqaAlertsFWBrowserWindowsPage extends DemoqaAlertsFWPage {    private SelenideElement newTabButton = $("button#tabButton");    private SelenideElement newWindowButton = $("button#windowButton");    private SelenideElement newWindowMessageButton = $("button#messageWindowButton");    public DemoqaAlertsFWBrowserWindowsPage ClickOnNewTabButton()    {        WaitElementIsVisible(newTabButton).scrollTo().click();        return page(DemoqaAlertsFWBrowserWindowsPage.class);    }    public DemoqaAlertsFWBrowserWindowsPage ClickOnNewWindowButton()    {        WaitElementIsVisible(newWindowButton).scrollTo().click();        return page(DemoqaAlertsFWBrowserWindowsPage.class);    }    public DemoqaAlertsFWBrowserWindowsPage ClickOnNewWindowMessageButton()    {        WaitElementIsVisible(newWindowMessageButton).scrollTo().click();        return page(DemoqaAlertsFWBrowserWindowsPage.class);    }    public DemoqaAlertsFWBrowserWindowsPage CloseNewTabOrWindow()    {        Selenide.switchTo().window(1).close();        Selenide.switchTo().window(0);        return page(DemoqaAlertsFWBrowserWindowsPage.class);    }}