package pages.demoqa_alerts_frame_windows;import com.codeborne.selenide.SelenideElement;import org.openqa.selenium.By;import pages.base.BasePage;import pages.demoqa_alerts_frame_windows.alerts.DemoqaAlertsPage;import pages.demoqa_alerts_frame_windows.browser_windows.DemoqaBrowserWindowsPage;import static com.codeborne.selenide.Selenide.$;import static com.codeborne.selenide.Selenide.page;public class DemoqaAlertsFrameWindowsPage extends BasePage {    private final SelenideElement ALERTS_CATEGORY = $(By.            xpath("//span[@class='text' and contains(text(), 'Alerts')]"));    private final SelenideElement BROWSER_WINDOWS_CATEGORY = $(By.            xpath("//span[@class='text' and contains(text(), 'Browser Windows')]"));    public DemoqaAlertsPage clickOnAlertsCategory() {        waitElementIsVisible(ALERTS_CATEGORY).scrollTo().click();        return page(DemoqaAlertsPage.class);    }    public DemoqaBrowserWindowsPage clickOnBrowserWindowsCategory() {        waitElementIsVisible(BROWSER_WINDOWS_CATEGORY).scrollTo().click();        return page(DemoqaBrowserWindowsPage.class);    }}