package pages.demoqa_alerts_frame_windows.browser_windows;import com.codeborne.selenide.Selenide;import com.codeborne.selenide.SelenideElement;import pages.demoqa_alerts_frame_windows.DemoqaAlertsFrameWindowsPage;import static com.codeborne.selenide.Selenide.$;import static com.codeborne.selenide.Selenide.page;public class DemoqaBrowserWindowsPage extends DemoqaAlertsFrameWindowsPage {    private final SelenideElement NEW_TAB_BUTTON = $("button#tabButton");    private final SelenideElement NEW_WINDOW_BUTTON = $("button#windowButton");    private final SelenideElement NEW_WINDOW_MESSAGE_BUTTON = $("button#messageWindowButton");    public DemoqaBrowserWindowsPage сlickOnNewTabButton() {        waitElementIsVisible(NEW_TAB_BUTTON).scrollTo().click();        return page(DemoqaBrowserWindowsPage.class);    }    public DemoqaBrowserWindowsPage сlickOnNewWindowButton() {        waitElementIsVisible(NEW_WINDOW_BUTTON).scrollTo().click();        return page(DemoqaBrowserWindowsPage.class);    }    public DemoqaBrowserWindowsPage сlickOnNewWindowMessageButton() {        waitElementIsVisible(NEW_WINDOW_MESSAGE_BUTTON).scrollTo().click();        return page(DemoqaBrowserWindowsPage.class);    }    public DemoqaBrowserWindowsPage сloseNewTabOrWindow() {        Selenide.switchTo().window(1).close();        Selenide.switchTo().window(0);        return page(DemoqaBrowserWindowsPage.class);    }}