package pages.demoqa_elements;import com.codeborne.selenide.SelenideElement;import org.openqa.selenium.By;import pages.base.BasePage;import pages.demoqa_elements.buttons.DemoqaElementButtonsPage;import pages.demoqa_elements.textbox.DemoqaElementTextboxPage;import static com.codeborne.selenide.Selenide.$;import static com.codeborne.selenide.Selenide.page;public class DemoqaElementsPage extends BasePage {    private SelenideElement textboxCategory = $(By            .xpath("//span[@class='text' and contains(text(), 'Text Box')]"));    private SelenideElement buttonsCategory = $(By            .xpath("//span[@class='text' and contains(text(), 'Buttons')]"));    public DemoqaElementTextboxPage ClickOnTextboxCategory()    {        WaitElementIsVisible(textboxCategory).scrollTo().click();        return page(DemoqaElementTextboxPage.class);    }    public DemoqaElementButtonsPage ClickOnButtonsCategory()    {        WaitElementIsVisible(buttonsCategory).scrollTo().click();        return page(DemoqaElementButtonsPage.class);    }}