package pages.demoqa_elements;import com.codeborne.selenide.SelenideElement;import org.openqa.selenium.By;import pages.base.BasePage;import pages.demoqa_elements.buttons.DemoqaButtonsPage;import pages.demoqa_elements.textbox.DemoqaTextBoxPage;import static com.codeborne.selenide.Selenide.$;import static com.codeborne.selenide.Selenide.page;public class DemoqaElementsPage extends BasePage {    private final SelenideElement BUTTONS_CATEGORY = $(By.            xpath("//span[@class='text' and contains(text(), 'Buttons')]"));    private final SelenideElement TEXTBOX_CATEGORY = $(By.            xpath("//span[@class='text' and contains(text(), 'Text Box')]"));    public DemoqaButtonsPage clickOnButtonsCategory() {        waitElementIsVisible(BUTTONS_CATEGORY).scrollTo().click();        return page(DemoqaButtonsPage.class);    }    public DemoqaTextBoxPage clickOnTextboxCategory() {        waitElementIsVisible(TEXTBOX_CATEGORY).scrollTo().click();        return page(DemoqaTextBoxPage.class);    }}