package pages.demoqa_elements.textbox;import com.codeborne.selenide.SelenideElement;import org.openqa.selenium.By;import pages.base.BasePage;import static com.codeborne.selenide.Condition.text;import static com.codeborne.selenide.Selenide.*;public class DemoqaElementTextboxPage extends BasePage {    private static String fullNameText;    private static String emailText;    private static String currentAddressText;    private static String permanentAddressText;    private SelenideElement fullNameInput = $(By.id("userName"));    private SelenideElement emailInput = $(By.id("userEmail"));    private SelenideElement currentAddressInput = $(By.id("currentAddress"));    private SelenideElement permanentAddressInput = $(By.id("permanentAddress"));    private SelenideElement submitButton = $(By.id("submit"));    private SelenideElement fullNameValidateBlock = $(By.xpath("//p[@id='name']"));    private SelenideElement emailValidateBlock = $(By.xpath("//p[@id='email']"));    private SelenideElement currentAddressValidateBlock = $(By.xpath("//p[@id='currentAddress']"));    private SelenideElement permanentAddressValidateBlock = $(By.xpath("//p[@id='permanentAddress']"));    private DemoqaElementTextboxPage choiceAndEnterTextOnInput(String text, SelenideElement input)    {        WaitElementIsVisible(input).scrollTo().sendKeys(text);        return page(DemoqaElementTextboxPage.class);    }    private DemoqaElementTextboxPage checkTextWithValidateBlock(String text,                                                                     SelenideElement blockWithComparisonText)    {        WaitElementIsVisible(blockWithComparisonText).scrollTo().shouldBe(text(text));        return page(DemoqaElementTextboxPage.class);    }    public DemoqaElementTextboxPage ClickOnSubmitButton()    {        WaitElementIsVisible(submitButton).scrollTo().click();        return page(DemoqaElementTextboxPage.class);    }    public DemoqaElementTextboxPage EnterDataOnInputs(String fullName, String email,                                                      String currentAddress, String permanentAddress) {        fullNameText = fullName;        emailText = email;        currentAddressText = currentAddress;        permanentAddressText = permanentAddress;        this                .choiceAndEnterTextOnInput(fullName, fullNameInput)                .choiceAndEnterTextOnInput(email, emailInput)                .choiceAndEnterTextOnInput(currentAddress, currentAddressInput)                .choiceAndEnterTextOnInput(permanentAddress, permanentAddressInput);        return page(DemoqaElementTextboxPage.class);    }    public DemoqaElementTextboxPage CheckAppearData() {        this                .checkTextWithValidateBlock(fullNameText, fullNameValidateBlock)                .checkTextWithValidateBlock(emailText, emailValidateBlock)                .checkTextWithValidateBlock(currentAddressText, currentAddressValidateBlock)                .checkTextWithValidateBlock(permanentAddressText, permanentAddressValidateBlock);        return page(DemoqaElementTextboxPage.class);    }/*    public DemoqaElementTextboxPage EnterDataOnInputsAndCheck(String fullName, String email,                                                      String currentAddress, String permanentAddress) {        this                .choiceAndEnterTextOnInput(fullName, fullNameInput)                .choiceAndEnterTextOnInput(email, emailInput)                .choiceAndEnterTextOnInput(currentAddress, currentAddressInput)                .choiceAndEnterTextOnInput(permanentAddress, permanentAddressInput)                .clickOnSubmitButton()                .checkTextWithValidateBlock(fullName, fullNameValidateBlock)                .checkTextWithValidateBlock(email, emailValidateBlock)                .checkTextWithValidateBlock(currentAddress, currentAddressValidateBlock)                .checkTextWithValidateBlock(permanentAddress, permanentAddressValidateBlock);        return page(DemoqaElementTextboxPage.class);    }*/}