package pages.demoqa_home;import com.codeborne.selenide.Selectors;import com.codeborne.selenide.SelenideElement;import pages.base.BasePage;import pages.demoqa_elements.DemoqaElementsPage;import static com.codeborne.selenide.Selenide.$;import static com.codeborne.selenide.Selenide.page;public class DemoqaHomePage extends BasePage {    private SelenideElement elementsCard = $(Selectors.byText("Elements"));    public DemoqaElementsPage ClickOnElementsCard() {        WaitElementIsVisible(elementsCard).scrollTo().click();        return page(DemoqaElementsPage.class);    }}