package pages.demoqa_home;import com.codeborne.selenide.Selectors;import com.codeborne.selenide.SelenideElement;import pages.base.BasePage;import pages.demoqa_elements.DemoqaElementsPage;import static com.codeborne.selenide.Selenide.$;import static com.codeborne.selenide.Selenide.page;public class DemoqaHomePage extends BasePage {    private final SelenideElement ELEMENTS_CARD = $(Selectors.            byText("Elements"));    public DemoqaElementsPage clickOnElementsCard() {        waitElementIsVisible(ELEMENTS_CARD).scrollTo().click();        return page(DemoqaElementsPage.class);    }}