package test.demoqa_positive;import io.qameta.allure.Epic;import io.qameta.allure.Feature;import io.qameta.allure.Owner;import org.junit.jupiter.api.BeforeAll;import org.junit.jupiter.api.Test;import test.Configuration.Config;import test.steps.base.BasePageSteps;import test.steps.demoqa_alertsFW.alerts.DemoqaAlertsFWAlertsPageSteps;import test.steps.demoqa_alertsFW.browserWindows.DemoqaAlertsFWBrowserWindowsPageSteps;import test.steps.demoqa_alertsFW.DemoqaAlertsFWPageSteps;import test.steps.demoqa_asidebar.DemoqaAsidebarPageSteps;import test.steps.demoqa_elements.buttons.DemoqaElementButtonsPageSteps;import test.steps.demoqa_elements.textbox.DemoqaElementTextBoxPageSteps;import test.steps.demoqa_elements.DemoqaElementsPageSteps;import test.steps.demoqa_home.DemoqaHomePageSteps;import static constants.Constant.DEMOQA_HOME_PAGE;@Epic("Тестовое задание")@Feature("E2E тестирование сайта Demoqa.com")@Owner(value = "Ережепов Олжас")public class DemoqaPositiveTest extends Config {    @BeforeAll    public static void selenideSettingsSetup()    {        configuration.Config.selenideSettingsSetup();    }    @Test    public void demoqaPositiveTest() {        BasePageSteps.openWithUrl(DEMOQA_HOME_PAGE);        DemoqaHomePageSteps.ClickOnElements();        DemoqaElementsPageSteps.ClickOnTextBoxCategory();        DemoqaElementTextBoxPageSteps                .EnterFields("Василий Пупкин", "test@gmail.com",                        "ул. Пушкина, дом Колотушкина",                        "Дом, в котором меня нет");        DemoqaElementTextBoxPageSteps                .ClickOnSubmitButton();        DemoqaElementTextBoxPageSteps                .CheckDataSavedCorrectly();        DemoqaElementsPageSteps.ClickOnButtonsCategory();        DemoqaElementButtonsPageSteps                .ClickOnClickMeButton();        DemoqaElementButtonsPageSteps                .CheckTextThatAppears("You have done a dynamic click");        DemoqaElementButtonsPageSteps                .ClickOnButtonRightClickMe();        DemoqaElementButtonsPageSteps                .CheckTextThatAppears("You have done a right click");        DemoqaElementButtonsPageSteps                .ClickOnButtonDoubleClickMe();        DemoqaElementButtonsPageSteps                .CheckTextThatAppears("You have done a double click");        DemoqaAsidebarPageSteps.ClickOnAlertsFW_Category();        DemoqaAlertsFWPageSteps.ClickOnBrowserWindowsCategory();        DemoqaAlertsFWBrowserWindowsPageSteps                .ClickOnNewTabButton();        DemoqaAlertsFWBrowserWindowsPageSteps                .CloseNewTab();        DemoqaAlertsFWBrowserWindowsPageSteps                .ClickOnNewWindowButton();        DemoqaAlertsFWBrowserWindowsPageSteps                .CloseNewWindow();        DemoqaAlertsFWPageSteps.ClickOnAlertsCategory();        DemoqaAlertsFWAlertsPageSteps                .ClickButtonNextToText("Click Button to see alert");        DemoqaAlertsFWAlertsPageSteps                .CloseAlertDialogue();        DemoqaAlertsFWAlertsPageSteps                .ClickButtonNextToText("On button click, alert will appear after 5 seconds");        DemoqaAlertsFWAlertsPageSteps                .CloseAlertDialogue();        DemoqaAlertsFWAlertsPageSteps                .ClickButtonNextToText("On button click, confirm box will appear");        DemoqaAlertsFWAlertsPageSteps                .ClickOnYesInDialogue();        DemoqaAlertsFWAlertsPageSteps                .CheckTextAppers("You selected Ok");        DemoqaAlertsFWAlertsPageSteps                .ClickButtonNextToText("On button click, prompt box will appear");        DemoqaAlertsFWAlertsPageSteps                .EnterFieldWithData("Test name");        DemoqaAlertsFWAlertsPageSteps                .CheckTextAppers("You entered Test name");    }}