package test.functional.cases.positive;import io.qameta.allure.Epic;import io.qameta.allure.Feature;import io.qameta.allure.Owner;import org.junit.jupiter.api.BeforeAll;import org.junit.jupiter.api.Test;import test.functional.steps.base.BasePageSteps;import test.functional.steps.alerts_frame_windows.alerts.AlertsPageSteps;import test.functional.steps.alerts_frame_windows.browser_windows.BrowserWindowsPageSteps;import test.functional.steps.alerts_frame_windows.AlertsFrameWindowsPageSteps;import test.functional.steps.asidebar.AsidebarPageSteps;import test.functional.steps.elements.buttons.ButtonsPageSteps;import test.functional.steps.elements.textbox.TextBoxPageSteps;import test.functional.steps.elements.ElementsPageSteps;import test.functional.steps.home.HomePageSteps;import static constants.Constant.DEMOQA_HOME_PAGE;@Epic("Тестовое задание")@Feature("E2E тестирование сайта Demoqa.com")@Owner(value = "Ережепов Олжас")public class PositiveTest {    @BeforeAll    public static void selenideSettingsSetup() {        configuration.Config.selenideSettingsSetup();    }    @Test    public void positiveTest() {        BasePageSteps.openWithUrl(DEMOQA_HOME_PAGE);        HomePageSteps.clickOnElements();        ElementsPageSteps.clickOnTextBoxCategory();        TextBoxPageSteps.                enterFields("Василий Пупкин", "test@gmail.com",                        "ул. Пушкина, дом Колотушкина",                        "Дом, в котором меня нет");        TextBoxPageSteps.clickOnSubmitButton();        TextBoxPageSteps.checkDataSavedCorrectly();        ElementsPageSteps.clickOnButtonsCategory();        ButtonsPageSteps.clickOnButtonClickMe();        ButtonsPageSteps.checkTextThatAppears("You have done a dynamic click");        ButtonsPageSteps.clickOnButtonRightClickMe();        ButtonsPageSteps.checkTextThatAppears("You have done a right click");        ButtonsPageSteps.clickOnButtonDoubleClickMe();        ButtonsPageSteps.checkTextThatAppears("You have done a double click");        AsidebarPageSteps.ClickOnAlertsFW_Category();        AlertsFrameWindowsPageSteps.clickOnBrowserWindowsCategory();        BrowserWindowsPageSteps.clickOnNewTabButton();        BrowserWindowsPageSteps.closeNewTab();        BrowserWindowsPageSteps.clickOnNewWindowButton();        BrowserWindowsPageSteps.closeNewWindow();        AlertsFrameWindowsPageSteps.clickOnAlertsCategory();        AlertsPageSteps.                clickButtonNextToText("Click Button to see alert");        AlertsPageSteps.closeAlertDialogue();        AlertsPageSteps.                clickButtonNextToText("On button click, alert will appear after 5 seconds");        AlertsPageSteps.closeAlertDialogue();        AlertsPageSteps.                clickButtonNextToText("On button click, confirm box will appear");        AlertsPageSteps.clickOnYesInDialogue();        AlertsPageSteps.checkTextAppers("You selected Ok");        AlertsPageSteps.                clickButtonNextToText("On button click, prompt box will appear");        AlertsPageSteps.enterFieldWithData("Test name");        AlertsPageSteps.checkTextAppers("You entered Test name");    }}