package test.functional.steps.alerts_frame_windows.alerts;import io.qameta.allure.Step;import static test.functional.configuration.Config.alertsPage;public class AlertsPageSteps {    @Step("Проверить, что появился текст {text}")    public static void checkTextAppers(String text) {        alertsPage.сheckText(text);    }    @Step("Нажать на кнопку «Click me»  рядом с {buttonName}")    public static void clickButtonNextToText(String buttonName) {        alertsPage.clickButtonNextToText(buttonName);    }    @Step("Нажать на кнопку «Да» в уведомление")    public static void clickOnYesInDialogue() {        alertsPage.acceptConfirmDialogue();    }    @Step("Закрыть уведомление")    public static void closeAlertDialogue() {        alertsPage.closeAlertDialogue();    }    @Step("Заполнить поле в уведомление данными: {promptData}")    public static void enterFieldWithData(String promptData) {        alertsPage.enterDataOnPrompt(promptData);    }}