package test.functional.steps.elements.buttons;import io.qameta.allure.Step;import static test.functional.configuration.Config.buttonsPage;public class ButtonsPageSteps {    @Step("Проверить, что появился текст {text}")    public static void checkTextThatAppears(String text) {        buttonsPage.checkTextThatAppears(text);    }    @Step("Нажать на кнопку «Click me»")    public static void clickOnButtonClickMe() {        buttonsPage.clickOnButtonClickMe();    }    @Step("Нажать на кнопку «Double Click me»")    public static void clickOnButtonDoubleClickMe() {        buttonsPage.clickOnButtonDoubleClickMe();    }    @Step("Нажать на кнопку «Right Click me»")    public static void clickOnButtonRightClickMe() {        buttonsPage.clickOnButtonRightClickMe();    }}