package test.functional.steps.elements.textbox;import io.qameta.allure.Step;import static test.functional.configuration.Config.textBoxPage;public class TextBoxPageSteps {    @Step("Проверить, что данные в блоке сохранены корректно")    public static void checkDataSavedCorrectly() {        textBoxPage.checkAppearData();    }    @Step("Нажать на кнопку «Submit»")    public static void clickOnSubmitButton() {        textBoxPage.clickOnSubmitButton();    }    @Step("Заполнить поля: Full Name, Email, Current Address, Permanent Address")    public static void enterFields(String fullName,                                   String email, String currentAddress,                                   String permanentAddress) {        textBoxPage.enterDataOnInputs(fullName, email,                                      currentAddress, permanentAddress);    }}