package test.steps.demoqa_alertsFW.alerts;import io.qameta.allure.Step;import static test.Configuration.Config.demoqaAlertsFWAlertsPage;public class DemoqaAlertsFWAlertsPageSteps {    @Step("Нажать на кнопку «Click me»  рядом с {buttonName}")    public static void ClickButtonNextToText(String buttonName) {        demoqaAlertsFWAlertsPage                .ClickButtonNextToText(buttonName);    }    @Step("Закрыть уведомление")    public static void CloseAlertDialogue() {        demoqaAlertsFWAlertsPage                .CloseAlertDialogue();    }    @Step("Нажать на кнопку «Да» в уведомление")    public static void ClickOnYesInDialogue() {        demoqaAlertsFWAlertsPage                .AcceptConfirmDialogue();    }    @Step("Проверить, что появился текст {text}")    public static void CheckTextAppers(String text) {        demoqaAlertsFWAlertsPage                .CheckText(text);    }    @Step("Заполнить поле в уведомление данными: {promptData}")    public static void EnterFieldWithData(String promptData) {        demoqaAlertsFWAlertsPage                .EnterDataOnPrompt(promptData);    }}