package test.steps.demoqa_elements.buttons;import io.qameta.allure.Step;import static test.Configuration.Config.demoqaElementButtonsPage;public class DemoqaElementButtonsPageSteps {    @Step("Нажать на кнопку «Click me»")    public static void ClickOnClickMeButton() {        demoqaElementButtonsPage.clickOnButtonClickMe();    }    @Step("Проверить, что появился текст {text}")    public static void CheckTextThatAppears(String text){       demoqaElementButtonsPage.checkTextThatAppears(text);    }    @Step("Нажать на кнопку «Right Click me»")    public static void ClickOnButtonRightClickMe() {        demoqaElementButtonsPage.clickOnButtonRightClickMe();    }    @Step("Нажать на кнопку «Double Click me»")    public static void ClickOnButtonDoubleClickMe() {        demoqaElementButtonsPage.clickOnButtonDoubleClickMe();    }}