package test.steps.demoqa_elements.textbox;import io.qameta.allure.Step;import static test.Configuration.Config.demoqaElementTextboxPage;public class DemoqaElementTextBoxPageSteps {    @Step("Заполнить поля: Full Name, Email, Current Address, Permanent Address")    public static void EnterFields(String fullName, String email,                                   String currentAddress, String permanentAddress) {        demoqaElementTextboxPage                .EnterDataOnInputs(fullName, email,                                            currentAddress, permanentAddress);    }    @Step("Нажать на кнопку «Submit»")    public static void ClickOnSubmitButton() {        demoqaElementTextboxPage.ClickOnSubmitButton();    }    @Step("Проверить, что данные в блоке сохранены корректно")    public static void CheckDataSavedCorrectly() {        demoqaElementTextboxPage.CheckAppearData();    }}